angular.module("headerController",["ngMaterial","WPAPI"]).controller("HeaderCtrl",["$scope","$rootScope","$mdSidenav","$mdMenu","UserService","MenuService","PostService",function($scope,$rootScope,$mdSidenav,$mdMenu,UserService,MenuService,PostService){$scope.$mdMenu=$mdMenu;$scope.openMenu=function($mdOpenMenu,$event){$mdOpenMenu($event);angular.element(document).bind("mousemove",function(e){var target;target=angular.element(e.target);if(target[0].tagName==="MD-BACKDROP"&&target.hasClass("md-menu-backdrop")){return $mdMenu.hide()}})};$scope.$mdSidenav=$mdSidenav;$scope.current_user_id=CURRENT_USER_ID;if(CURRENT_USER_ID==="0"){$rootScope.current_user=null}else{$scope.adminUrl=BLOG_URL+"/wp-admin/";$rootScope.current_user=UserService.get({})}$scope.keyword="";$scope.search=function(){var search_url;if($scope.keyword.length!==0){search_url="https://www.google.com/search?q=site%3A"+BLOG_BARE_URL+"+"+$scope.keyword;return location.href=search_url}};$scope.menu=MenuService.get();return $scope.recentPosts=PostService.query()}]);